            method:'POST',
            headers: {'Content-Type':'application/json'},
            body: JSON.stringify(newComment)
        }).then(()=>{
            fe
        });
    };

    const fetchComments=()=>{
        fetch('http://127.0.0.1:8000/comments/?shayari=${shayariId}')
        .then(response=>response.json())
        .then(data=>{
            const fetched_comments = data.map(elem=>({
                id:elem.id,
                shayari:elem.shayari,
                content:elem.content
            }));
            setComments(fetched_comments);
        })
    };
    useEffect(()=>{fetchComments()},[shayariId]);


    return (
        <div className="comments">
            <form onSubmit={handleSubmit} class='add-comment-form'>
                <input placeholder="add comments :)"  value ={content} onChange={ (e)=>setContent(e.target.value)}></input>
                <button type="submit">Add Shayari</button>
            </form>
                {comments.map((elem,idx) =>(
                    <div className="comment" key={idx}>
                        <p>{elem.content}</p>
                    </div>
                ))}
        </div>
    );
}
export default Comments
