import React, { useEffect, useState } from "react";

function Comments({shayariId, showcomments, setShowComments}) {
    const [comments, setComments] = useState([]);
    const [content, setContent] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const newComment = { content, shayari: shayariId };
        fetch('http://127.0.0.1:8000/comments/', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(newComment)
        }).then(() => {
            setContent('');
            fetchComments();
        });
    };

    const fetchComments = () => {
        fetch(`http://127.0.0.1:8000/comments/?shayari=${shayariId}`)
        .then(response => response.json())
        .then(data => {
            const fetched_comments = data.map(elem => ({
                id: elem.id,
                shayari: elem.shayari,
                content: elem.content
            }));
            setComments(fetched_comments);
        });
    };


    return (
        <>
        {showcomments &&( <div className="comments">
            <form onSubmit={handleSubmit} className='add-comment-form'>
                <input 
                    placeholder="Add comments :)"
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                />
                <button type="submit">Add Comment</button>
            </form>
            {comments.map((elem, idx) => (
                <div className="comment" key={idx}>
                    <p>{elem.content}</p>
                </div>
            ))}
        </div>)}
        </>
    );
}

export default Comments;
