import React, { useState } from 'react';

function AddShayari({ onAdd }) {
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [content, setContent] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const newShayari = { title, author, content };
        onAdd(newShayari);
        setTitle('');
        setAuthor('');
        setContent('');
    };

    return (
        <div>
            <h1>Add Shayari</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    placeholder="Title"
                    required
                />
                <input
                    type="text"
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}
                    placeholder="Author"
                    required
                />
                <textarea
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                    placeholder="Content"
                    required
                />
                <button type="submit">Add Shayari</button>
            </form>
        </div>
    );
}

export default AddShayari;
