import React from 'react';

function like_shayari(id, setShayari, shayari) {
    fetch(`http://127.0.0.1:8000/shayari/${id}/like/`, {
        method: 'POST',
    })
    .then(response => {
        if (!response.ok) {
            throw new Error('Network response was not ok');
        }
        return response.json();
    })
    .then(data => {
        // Update the likes in the state directly
        const updatedShayari = shayari.map(item => {
            if (item.id === id) {
                return { ...item, likes: data.likes };
            }
            return item;
        });
        setShayari(updatedShayari);
    })
    .catch(error => console.error('Error:', error));
}

function Shayari({ id, title, author, content, likes, dislikes, setShayari, shayari }) {
    return (
        <div className='shayari-card'>
            <h2>{title}</h2>
            <h4>{author}</h4>
            <p>{content}</p>
            <p>
                <button onClick={() => like_shayari(id, setShayari, shayari)}>Like</button>
                {likes}
            </p>
            <p>Dislikes: {dislikes}</p>
        </div>
    );
}

export default Shayari;
