import React, { useState } from 'react';

function AddShayari({ onAdd }) {
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [content, setContent] = useState('');
    const [submit, setSubmit] = useState(false);

    const handleSubmit = (e) => {
        e.preventDefault();
        const newShayari = { title, author, content };
        fetch('http://127.0.0.1:8000/shayaris/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newShayari),
        })
        .then(response => {
            if (response.ok) {
                setSubmit(true);
                setTitle('');
                setAuthor('');
                setContent('');
            }
        })
        .catch(error => console.error('Error:', error));
    };

    return (
        <div className="add-shayari-card">
            <form onSubmit={handleSubmit} className="add-shayari-form">
                <h2 className="form-title">अपनी शायरी जोड़ें</h2>
                <input
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    placeholder="शायरी का शीर्षक..."
                    required
                />
                <input
                    type="text"
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}
                    placeholder="रचनाकार का नाम..."
                    required
                />
                <textarea
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                    placeholder="अपनी भावनाएँ यहाँ लिखें..."
                    required
                />
                <button type="submit">शायरी जोड़ें</button>
                {submit && <p className="submitted-message">आपकी शायरी सफलतापूर्वक जोड़ी गई!</p>}
            </form>
        </div>
    );
}

export default AddShayari;
