import React, { useEffect, useState } from "react";
import Shayari from './Shayari';

function ShayariList() {
    const [shayari, setShayari] = useState([]);
    const [change, setChange] = useState(false);

    useEffect(() => {
        const fetchShayaris = () => {
            const url = 'http://127.0.0.1:8000/shayaris/';
            fetch(url)
                .then(response => {
                    if (!response.ok) {
                        throw new Error('Network response was not ok');
                    }
                    return response.json();
                })
                .then(data => {
                    const fetched_shayaris = data.map(item => ({
                        id: item.id,
                        title: item.title,
                        author: item.author,
                        content: item.content,
                        likes: item.like_count,
                        dislikes: item.dislike_count
                    }));
                    setShayari(fetched_shayaris);
                })
                .catch(error => console.error('Error:', error));
        };

        fetchShayaris();
    }, [change]);

    return (
        <div>
            <div className="shayari-list">
                {shayari.map((elem, idx) => (
                    <Shayari 
                        key={idx} 
                        id={elem.id} 
                        title={elem.title} 
                        author={`- ${elem.author}`}
                        content={elem.content} 
                        likes={elem.likes} 
                        dislikes={elem.dislikes} 
                        setShayari={setShayari} 
                        shayari={shayari} 
                    />
                ))}
            </div>
        </div>
    );
}

export default ShayariList;
