import React, { useEffect, useState } from "react";
import Shayari from './Shayari';
import AddShayari from './AddShayari';
import SearchBar from './SearchBar';

function ShayariList() {
    const [shayari, setShayari] = useState([]);
    const [searchFlag, setSearchFlag] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');

    useEffect(() => {
        const fetchShayaris = () => {
            const url = searchFlag 
                ? `http://127.0.0.1:8000/shayaris/?search=${encodeURIComponent(searchQuery)}`
                : 'http://127.0.0.1:8000/shayaris/';

            fetch(url)
                .then(response => {
                    if (!response.ok) {
                        throw new Error('Network response was not ok');
                    }
                    return response.json();
                })
                .then(data => {
                    console.log('Data received from server:', data);
                    setShayari(data);
                })
                .catch(error => console.error('Error:', error));
        };

        fetchShayaris();
    }, [searchFlag, searchQuery]);



    const addShayari = (newShayari) => {
        setShayari([...shayari, newShayari]);
    };

    return (
        <div>

            <div className="shayari-list">
                {shayari.map((elem, idx) => (
                    <Shayari key={idx} title={elem.title} author={`- ${elem.author}`}
                    content={elem.content} />
                ))}
            </div>
        </div>
    );
}

export default ShayariList;
