// CommentSection.js
import React, { useEffect, useState, useContext } from 'react';
import { AuthContext } from './AuthContext';

function CommentSection({ shayariId, showcomments, setShowComments }) {
    const [comments, setComments] = useState([]);
    const [content, setContent] = useState('');
    const { authTokens } = useContext(AuthContext);

    useEffect(() => {
        if (showcomments) {
            fetchComments();
        }
    }, [showcomments]);

    const fetchComments = () => {
        fetch(`http://127.0.0.1:8000/comments/?shayari=${shayariId}`)
            .then((response) => response.json())
            .then((data) => {
                setComments(data);
            })
            .catch((error) => console.error('Error fetching comments:', error));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const newComment = { content, shayari: shayariId };
        fetch('http://127.0.0.1:8000/comments/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${authTokens?.access}`,
            },
            body: JSON.stringify(newComment),
        })
            .then((response) => {
                if (response.ok) {
                    setContent('');
                    fetchComments();
                } else {
                    alert('Failed to post comment.');
                }
            })
            .catch((error) => console.error('Error posting comment:', error));
    };

    return (
        <>
            {showcomments && (
                <div className="comment-section">
                    <button className="close-button" onClick={() => setShowComments(false)}>&times;</button>
                    <h3>Comments</h3>
                    {authTokens ? (
                        <form onSubmit={handleSubmit} className='add-comment-form'>
                            <input
                                placeholder="Add a comment..."
                                value={content}
                                onChange={(e) => setContent(e.target.value)}
                            />
                            <button type="submit">Post Comment</button>
                        </form>
                    ) : (
                        <p>Please log in to add a comment.</p>
                    )}
                    {comments.length > 0 ? (
                        comments.map((elem) => (
                            <div className="comment" key={elem.id}>
                                <p><strong>{elem.user}:</strong> {elem.content}</p>
                            </div>
                        ))
                    ) : (
                        <p>No comments yet.</p>
                    )}
                </div>
            )}
        </>
    );
}

export default CommentSection;
