// Shayari.js

import React, { useState, useContext } from 'react';
import CommentSection from './CommentSection';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp, faThumbsDown, faCommentDots } from '@fortawesome/free-solid-svg-icons';
import { AuthContext } from './AuthContext';

function Shayari(props) {
    const [showcomments, setShowComments] = useState(false);
    const { authTokens } = useContext(AuthContext);

    return (
        <div className='shayari-card'>
            <h2>{props.title}</h2>
            <h4>{props.author}</h4>
            <p>“{props.content}”</p>
            <div className="shayari-actions">
                <button onClick={props.onLike} disabled={!authTokens}>
                    <FontAwesomeIcon icon={faThumbsUp} />
                    <span>{props.likes}</span>
                </button>
                <button onClick={props.onDislike} disabled={!authTokens}>
                    <FontAwesomeIcon icon={faThumbsDown} />
                    <span>{props.dislikes}</span>
                </button>
                <button onClick={() => setShowComments(!showcomments)}>
                    <FontAwesomeIcon icon={faCommentDots} />
                </button>
            </div>
            {showcomments && (
                <CommentSection shayariId={props.id} showcomments={showcomments} setShowComments={setShowComments} />
            )}
        </div>
    );
}

export default Shayari;
