function like_shayari(id, setShayari, shayari) {
    fetch(`http://127.0.0.1:8000/shayari/${id}/like/`, {
        method: 'POST',
    })
    .then(response => {
        if (!response.ok) {
            throw new Error('Network response was not ok');
        }
        return response.json();
    })
    .then(data => {
        // Find the shayari that was liked and update its like count in the local state
        const updatedShayari = shayari.map(item => {
            if (item.id === id) {
                return { ...item, likes: data.likes };
            }
            return item;
        });
        setShayari(updatedShayari);
    })
    .catch(error => console.error('Error:', error));
}
