import React, { useState } from 'react';

function AddShayari({ onAdd }) {
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [content, setContent] = useState('');
    const [flag, setFlag] = useState(false);
    const handleSubmit = (e) => {
        e.preventDefault();

        const newShayari = {
            title,
            author,
            content
        };

        fetch('http://127.0.0.1:8000/shayaris/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newShayari), // here we are attaching the body as newShayari!
        })
        .then(response => response.json())
        .then(data => {
            onAdd(data);  // Callback to update the list
            setTitle('');
            setAuthor('');
            setContent('');
            setFlag(false);
        })
    };

    return (
        <div>
        <button onClick={()=>{setFlag(!flag)}}>Add Shayari</button>
        {flag &&(<form onSubmit={handleSubmit}>
            <div>
                <label>Title:</label>
                <input
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
            </div>
            <div>
                <label>Author:</label>
                <input
                    type="text"
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}
                    required
                />
            </div>
            <div>
                <label>Content:</label>
                <textarea
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                    required
                />
            </div>
            <button type="submit">Submit</button>
        </form>)}

        </div>
    );
}

export default AddShayari;
