import React, { useState } from 'react';
import Comments from "./CommentSection";

function Shayari(props) {
    const [showcomments, setShowComments] = useState(false);

    return (
        <div className='shayari-card'>
            <h2>{props.title}</h2>
            <h4>{props.author}</h4>
            <p>{props.content}</p>
            <div className="shayari-actions">
                <button className="like-button" onClick={props.onLike}></button> {props.likes}
                <button className="dislike-button" onClick={props.onDislike}></button> {props.dislikes}
                <button className="comment-button" onClick={() => setShowComments(!showcomments)}>
                    {showcomments ? "Hide Comments" : "Comment"}
                </button>
                {showcomments && (
                    <Comments shayariId={props.id} showcomments={showcomments} setShowComments={setShowComments} />
                )}
            </div>
        </div>
    );
}

export default Shayari;
