import React, { useEffect, useState } from "react";
import Shayari from './Shayari';
import Comments from "./CommentSection";
function ShayariList() {
    const [shayari, setShayari] = useState([]);
    const [showcomments, setShowComments] = useS
    useEffect(() => {
        fetch('http://127.0.0.1:8000/shayaris/')
            .then(response => response.json())
            .then(data => {
                const fetchedShayaris = data.map(item => ({
                    id: item.id,
                    title: item.title,
                    author: item.author,
                    content: item.content,
                    likes: item.like_count,
                    dislikes: item.dislike_count
                }));
                setShayari(fetchedShayaris);
            })
            .catch(error => console.error('Error:', error));
    }, []);

    const handleLike = (id) => {
        setShayari(prevShayaris =>
            prevShayaris.map(shayari =>
                shayari.id === id ? { ...shayari, likes: shayari.likes + 1 } : shayari
            )
        );

        fetch(`http://127.0.0.1:8000/shayari/${id}/like/`, {
            method: 'POST',
        })
        .then(response => response.json())
        .catch(error => console.error('Error:', error));
    };

    const handleDislike = (id) =>{
        setShayari(prevShayari =>
            prevShayari.map(elem =>{
                if (elem.id === id) {
                    return { ...elem, dislikes: elem.dislikes + 1 };
                } else {
                    return elem;
                }
            })
        )
        fetch(`http://127.0.0.1:8000/shayari/${id}/dislike/`, {
            method: 'POST',
        })
        .then(response => response.json())
        .catch(error => console.error('Error:', error));
    };


    function ShayariCard({elem, idx}){
        return (
            <div key = {idx} >
                <Shayari 
                        id={elem.id} 
                        title={elem.title} 
                        author={`- ${elem.author}`}
                        content={elem.content} 
                        likes={elem.likes} 
                        dislikes={elem.dislikes} 
                        onLike={() => handleLike(elem.id)}
                        onDislike={() => handleDislike(elem.id)}
                    />
                <Comments shayariId={elem.id} />
            </div>
        )
    }

    return (
        <div className="shayari-list">
            {shayari.map((elem, idx) => (
                <ShayariCard elem={elem} idx={idx}/>
            ))}
        </div>
    );
}

export default ShayariList;
