import React, { useState } from 'react';

function AddShayari({ onAdd }) {
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [content, setContent] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const newShayari = { title, author, content };

    };

    return (
        <div className="add-shayari-card">
            <form onSubmit={handleSubmit} className="add-shayari-form">
                <input
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    placeholder="शायरी का शीर्षक..."
                    required
                />
                <input
                    type="text"
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}
                    placeholder="रचनाकार का नाम.."
                    required
                />
                <textarea
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                    placeholder="अपनी भावनाएँ यहाँ उकेरें.."
                    required
                />
                <button type="submit">Add Shayari</button>
            </form>
        </div>
    );
}

export default AddShayari;
