import React, { useEffect, useState } from "react";
import Shayari from './Shayari';
import AddShayari from './AddShayari';

function ShayariList() {
    const [shayari, setShayari] = useState([]);

    useEffect(() => {
        const fetchShayaris = () => {
            fetch('http://127.0.0.1:8000/shayaris/')
                .then(response => {
                    if (!response.ok) {
                        throw new Error('Network response was not ok');
                    }
                    return response.json();
                })
                .then(data => {
                    console.log('Data received from server:', data);
                    data.forEach((item) => {
                        const fetched_shayari = {
                            title: item.title,
                            author: item.author,
                            content: item.content,
                        };
                        setShayari(prevShayaris => [...prevShayaris, fetched_shayari]);
                    });
                })
                .catch(error => console.error('Error:', error));
        };

        fetchShayaris();
    }, []);

    return (
        <div>
            <div className="shayari-list">
                {shayari.map((elem, idx) => (
                    <Shayari key={idx} title={elem.title} author={`- ${elem.author}`} content={elem.content} />
                ))}
            </div>
        </div>
    );
}

export default ShayariList;
